mapper.update:
update SYS_AUTHZ_ROLE_LIST t

	t.R_NAME = #{name},
	t.R_TYPE = #{type},
	t.R_INTRO = #{intro}

where t.R_ID = #{id}

UPDATE SYS_AUTHZ_ROLE_LIST 
   SET R_STATUS = #{status}
 WHERE R_ID = #{roleId}

mapper.select:
SELECT t.R_ID, t.R_NAME, t.R_TYPE, t.R_INTRO, t.R_STATUS, (SELECT COUNT(x.R_ID) FROM SYS_AUTHZ_USER_ROLE_RELATION x WHERE x.R_ID = t.R_ID) as R_USERS, t.R_TIME24 FROM SYS_AUTHZ_ROLE_LIST t
and t.R_STATUS = #{status}
and t.R_TYPE = #{type}
and t.R_NAME like '%'|| #{name} ||'%'

SELECT t.U_ID, t.U_USERNAME, '******' AS U_PASSWORD, t.U_AVATAR, t.U_PHONE, t.U_EMAIL, t.U_REMARK, t.U_STATUS, t.U_TIME24 FROM SYS_AUTHZ_USER_LIST t INNER JOIN SYS_AUTHZ_USER_ROLE_RELATION x ON t.U_ID = x.U_ID WHERE x.R_ID = #{id}
and t.U_STATUS = #{status}
and t.U_USERNAME like '%'|| #{username} ||'%'

SELECT t.U_ID, t.U_USERNAME, '******' AS U_PASSWORD, t.U_AVATAR, t.U_PHONE, t.U_EMAIL, t.U_REMARK, t.U_STATUS, t.U_TIME24 FROM SYS_AUTHZ_USER_LIST t WHERE NOT EXISTS ( SELECT '1' FROM SYS_AUTHZ_USER_ROLE_RELATION z WHERE t.U_ID = z.U_ID and z.R_ID = #{id} )
and t.U_STATUS = #{status}
and t.U_USERNAME like '%'|| #{username} ||'%'
