mapper.update:
update SYS_AUTHZ_USER_LIST t
t.U_ALIAS = #{alias}, t.U_AVATAR = #{avatar}, t.U_PHONE = #{phone}, t.U_EMAIL = #{email}, t.U_REMARK = #{remark}
where t.U_ID = #{id}

update SYS_AUTHZ_USER_DETAIL t
t.D_GENDER = #{gender}, t.D_BIRTHDAY = #{birthday}, t.D_IDCARD = #{idcard},
where t.U_ID = #{id}

update SYS_AUTHZ_USER_LIST t 
   set t.U_PASSWORD = #{password}
where t.U_ID = #{userId}

update SYS_AUTHZ_USER_LIST t 
   set t.U_PASSWORD = #{password}
where t.U_ID = #{userId} and t.U_PASSWORD = #{oldPassword}

UPDATE SYS_AUTHZ_USER_LIST t
   SET t.U_STATUS = #{status}
 WHERE t.U_ID = #{userId}

mapper.select:
SELECT t.U_ID, t.U_USERNAME, t.U_ALIAS, '******' AS U_PASSWORD, t.U_AVATAR, t.U_PHONE, t.U_EMAIL, t.U_REMARK, t.U_STATUS, t.U_TIME24, x.D_ID, x.D_AGE, x.D_BIRTHDAY, x.D_CODE, x.D_GENDER, x.D_IDCARD, (SELECT wm_concat(y.R_ID) R_ID FROM SYS_AUTHZ_USER_ROLE_RELATION y WHERE y.U_ID = t.U_ID ) R_ID, (SELECT wm_concat(z.R_NAME) R_NAME FROM SYS_AUTHZ_USER_ROLE_RELATION y INNER JOIN SYS_AUTHZ_ROLE_LIST z ON y.R_ID = z.R_ID WHERE y.U_ID = t.U_ID ) R_NAME FROM SYS_AUTHZ_USER_LIST t INNER JOIN SYS_AUTHZ_USER_DETAIL x ON t.U_ID = x.U_ID LEFT JOIN SYS_AUTHZ_USER_ROLE_RELATION y ON y.U_ID = t.U_ID WHERE t.U_USERNAME != 'admin'
and t.U_STATUS = #{model.status}
and t.U_USERNAME like '%'|| #{model.username} ||'%'
and exists ( SELECT y.U_ID FROM SYS_AUTHZ_USER_ROLE_RELATION y WHERE y.U_ID = t.U_ID AND y.R_ID = #{model.roleId} )
and x.D_GENDER = #{model.gender}
and x.D_IDCARD like '%'|| #{model.idcard}||'%'

SELECT t.R_ID, t.R_NAME, t.R_TYPE, t.R_INTRO, t.R_STATUS, (SELECT COUNT(x.R_ID) FROM SYS_AUTHZ_USER_ROLE_RELATION x WHERE x.R_ID = t.R_ID) as R_USERS, t.R_TIME24 FROM SYS_AUTHZ_ROLE_LIST t INNER JOIN SYS_AUTHZ_USER_ROLE_RELATION x ON t.R_ID = x.R_ID WHERE x.U_ID = #{id}
and t.R_STATUS = #{model.status}
and t.R_TYPE = #{model.type}
and t.R_NAME like '%'|| #{model.name} ||'%'

SELECT t.R_ID, t.R_NAME, t.R_TYPE, t.R_INTRO, t.R_STATUS, (SELECT COUNT(x.R_ID) FROM SYS_AUTHZ_USER_ROLE_RELATION x WHERE x.R_ID = t.R_ID) as R_USERS, t.R_TIME24 FROM SYS_AUTHZ_ROLE_LIST t WHERE NOT EXISTS ( SELECT '1' FROM SYS_AUTHZ_USER_ROLE_RELATION z WHERE t.R_ID = z.R_ID and z.U_ID = #{id})
and t.R_STATUS = #{model.status}
and t.R_TYPE = #{model.type}
and t.R_NAME like '%'|| #{model.name} ||'%'
