mapper.update:
update SYS_AUTHZ_USER_LIST t
t.U_ALIAS = #{alias}, t.U_AVATAR = #{avatar}, t.U_PHONE = #{phone}, t.U_EMAIL = #{email}, t.U_REMARK = #{remark}
where t.U_ID = #{userId}

update SYS_AUTHZ_USER_DETAIL t
t.D_GENDER = #{gender}, t.D_BIRTHDAY = #{birthday}, t.D_IDCARD = #{idcard},
where t.U_ID = #{userId}

update SYS_AUTHZ_USER_LIST t 
   set t.U_PASSWORD = #{password}
where t.U_ID = #{userId}

update SYS_AUTHZ_USER_LIST t 
   set t.U_PASSWORD = #{password}
where t.U_ID = #{userId} and t.U_PASSWORD = #{oldPassword}

mapper.select:
SELECT t.U_ID, t.U_USERNAME, t.U_ALIAS, '******' AS U_PASSWORD, t.U_AVATAR, t.U_PHONE, t.U_EMAIL, t.U_REMARK, t.U_STATUS, t.U_TIME24, x.D_ID, x.D_BIRTHDAY, x.D_GENDER, x.D_IDCARD, y.R_ID FROM SYS_AUTHZ_USER_LIST t INNER JOIN SYS_AUTHZ_USER_DETAIL x ON t.U_ID = x.U_ID LEFT JOIN SYS_AUTHZ_USER_ROLE_RELATION y ON y.U_ID = t.U_ID
and t.U_STATUS = #{status}
and x.D_GENDER = #{gender}
and t.U_USERNAME like '%'|| #{username} ||'%'

SELECT t.R_ID, t.R_NAME, t.R_TYPE, t.R_INTRO, t.R_STATUS, (SELECT COUNT(x.R_ID) FROM SYS_AUTHZ_USER_ROLE_RELATION x WHERE x.R_ID = t.R_ID) as R_USERS, t.R_TIME24 FROM SYS_AUTHZ_ROLE_LIST t INNER JOIN SYS_AUTHZ_USER_ROLE_RELATION x ON t.R_ID = x.R_ID WHERE x.U_ID = #{id}
and t.R_STATUS = #{status}
and t.R_TYPE = #{type}
and t.R_NAME like '%'|| #{name} ||'%'

SELECT t.R_ID, t.R_NAME, t.R_TYPE, t.R_INTRO, t.R_STATUS, (SELECT COUNT(x.R_ID) FROM SYS_AUTHZ_USER_ROLE_RELATION x WHERE x.R_ID = t.R_ID) as R_USERS, t.R_TIME24 FROM SYS_AUTHZ_ROLE_LIST t WHERE NOT EXISTS ( SELECT '1' FROM SYS_AUTHZ_USER_ROLE_RELATION z WHERE t.R_ID = z.R_ID and z.U_ID = #{id})
and t.R_STATUS = #{status}
and t.R_TYPE = #{type}
and t.R_NAME like '%'|| #{name} ||'%'
